    private void enviarButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enviarButtonActionPerformed
        boolean verifEmail = DaoPessoa.verifEmail(emailTextField.getText());
        if (verifEmail){
            DaoPessoa.recuperacaoSenha(emailTextField.getText());
            try{
                Email.Email.sendEmail(emailTextField.getText());
            }catch(Exception e){
                e.printStackTrace();
            }
            new MsgBox("E-mail enviado para "+emailTextField.getText()).setVisible(true);
        }else{
            new MsgBox(emailTextField.getText()+" n√£o encontrado").setVisible(true);
        }
    }//GEN-LAST:event_enviarButtonActionPerformed

